import './App.css';
import "https://unpkg.com/elm-pep@1.0.6/dist/elm-pep.js";
import React from 'react';
import PropTypes from 'prop-types';
import Map from 'ol/Map';
import View from 'ol/View';
import Tile from 'ol/layer/Tile';
import Vector from 'ol/layer/Vector';
import { fromLonLat } from 'ol/proj';
import Stroke from 'ol/style/Stroke';
import interaction from 'ol/interaction';
import Source from 'ol/source';
import OSM from "ol/source/OSM";
import Style from 'ol/style/Style';
import Circle from 'ol/style/Circle';
import Fill from 'ol/style/Fill';

console.clear();
const App = () => <MapApp><Polygon /></MapApp>;

class MapApp extends React.Component {
	constructor(props) {
    super(props);

		// Define base layer
  	this.baseLayer = new Tile({ source: new OSM() });

  	// Define base view
    this.baseView = new View({
      center: fromLonLat([37.41, 8.82]),
      zoom: 4
    });

		// Add map, base view and layer
    this.map = new Map({
      layers: [this.baseLayer],
      view: this.baseView
    });
  }

	static childContextTypes = {
  	map: PropTypes.object
  }

  getChildContext() {
  	return {
      map: this.map
    }
  }

  componentDidMount() {
    // set map target element
    this.map.setTarget(this.map);
  }

  render() {
  	return <div ref="map">
      {this.props.children}
    </div>;
  }
}

class Polygon extends React.Component {
  static contextTypes = {
    map: PropTypes.object
  }

  componentDidMount() {
  	const source = new Vector({wrapX: false});
    this.polygonBase = new Vector({ source });
    this.context.map.addLayer(this.polygonBase);

		const styleFunction = feature => {
      var geometry = feature.getGeometry();
      console.log('geometry', geometry.getType());
      if (geometry.getType() === 'LineString') {
        var styles = [
          new Style({
            stroke: new Stroke({
              color: 'rgba(255, 102, 0, 1)',
              width: 3
            })
          })
        ];
        return styles;
      }
			if (geometry.getType() === 'Point') {
        var styles = [
          new Style({
						image: new Circle({
      	    	radius: 5,
      	    	stroke: new Stroke({ color: 'rgba(255, 0, 0, 1)' }),
      	    	fill: new Fill({ color: 'rgba(255, 0, 0, 0.5)' })
						})
          })
        ];
        return styles;
      }
      if (geometry.getType() === 'Polygon') {
        var styles = [
          new Style({
            stroke: new Stroke({
              color: 'rgba(255, 102, 0, 0)',
              width: 3
            }),
            fill: new Fill({
              color: 'rgba(255, 102, 0, 0.3)'
            })
          })
        ];
        return styles;
      }
      return false;
    };

    // Define the polygon draw interaction
    this.draw = new interaction.Draw({
      source: source,
      type: 'Polygon',
			style: styleFunction
    });
    // Add the draw interaction
    this.context.map.addInteraction(this.draw);

		this.draw.on('drawstart', event => {
			this.polygonFirstCoordinate = event.feature.getGeometry().getFirstCoordinate();
		});
  }

  componentWillUnmount () {
    this.context.map.removeInteraction(this.draw);
  }

  render() { return <p>Drawing polygon</p>; }
}

// ReactDOM.render(
//   <App />,
//   document.getElementById('root')
// );


export default App;
