import "https://unpkg.com/elm-pep@1.0.6/dist/elm-pep.js";
import {React,useEffect,useState,useRef} from 'react';
import ReactDOM from 'react-dom'
import PropTypes from 'prop-types';
import Map from 'ol/Map';
import View from 'ol/View';
import Tile from 'ol/layer/Tile';
import Vector from 'ol/layer/Vector';
import { fromLonLat } from 'ol/proj';
import Stroke from 'ol/style/Stroke';
import interaction from 'ol/interaction';
import Draw from 'ol/interaction/Draw';
import Source from 'ol/source';
import OSM from "ol/source/OSM";
import Style from 'ol/style/Style';
import Circle from 'ol/style/Circle';
import Fill from 'ol/style/Fill';
import DrawMap from "./DrawMap";
import Layer from "ol/layer/Layer";
import './MapApp.css';

const MapApp = () => {
  const mapRef = useRef();

  const [map,setMap] = useState(null);

  const [type, setType] = useState("Point");

  const typeRef = useRef(null);

  const raster = new Tile({
    source: new OSM(),
  });
  
  const source = new Vector({wrapX: false});
  
  const vector = new Vector({
    source: source,
  });

  // on component mount
    useEffect(() => {
        let mapObject = new Map({
          layers: [raster, vector],
          target: 'map',
          view: new View({
            center: [-11000000, 4600000],
            zoom: 4,
          }),});
        mapObject.setTarget(mapRef.current);
        setMap(mapObject);

        return () => mapObject.setTarget(undefined);
    }, []);

    return (
      <div className={'map'} ref={mapRef}>

      <label>Geometry type: </label>
      <select ref={typeRef} value={type} onChange={(event) => setType(event.target.value)}>
          <option value="Point">Point</option>
          <option value="LineString">LineString</option>
          <option value="Polygon">Polygon</option>
          <option value="Circle">Circle</option>
          <option value="None">None</option>
      </select>
      <label>{type}</label>
      <input type="button" value="Undo" id="undo" onClick boolean="true"></input>
      <DrawMap type = {type} map = {map} />
      
  </div>
    )


}




