import React, { useState, useRef } from 'react'



//const typeRef = useRef();



const TypeSelect = () => {
    const [type, setType] = useState("point");

    const typeSet = (e) => {
        setType(typeRef.current.value);
    }

    const typeRef = typeRef(null);



    return (
        <div>
            <label class="input-group-text" for="type">Geometry type:</label>
            <select  value={type} onChange={typeSet}>
                <option value="Point">Point</option>
                <option value="LineString">LineString</option>
                <option value="Polygon">Polygon</option>
                <option value="Circle">Circle</option>
                <option value="None">None</option>
            </select>
            <label>{type}aaaa</label>
            
        </div>
    )
}

export default TypeSelect
